import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getQuestionList } from "../actions/getQuestionList";
import { setLoadingStatus } from "../actions/index";
import Loading from "../components/Loading";

const GamePage = ({
  getQuestionList,
  difficulty,
  questionList,
  loading,
  setLoadingStatus,
}) => {
  useEffect(() => {
    setLoadingStatus(true);
    getQuestionList(difficulty);
  }, []);

  const answersArray = [
    ...questionList[0].incorrect_answers,
    questionList[0].correct_answer,
  ];

  if (questionList[0] != undefined) {
    setLoadingStatus(false);
  }

  if (loading === true) {
    return <Loading />;
  } else {
    return (
      <div className="game-page">
        <div className="game-content">
          <div className="game-question">{questionList[0].question}</div>
        </div>
      </div>
    );
  }
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, { getQuestionList, setLoadingStatus })(
  GamePage
);
