import { combineReducers } from "redux";

const difficultyReducer = (state = "", action) => {
  action.type === "DIFFICULTY_LEVEL" ? action.payload : state;
};

const gameoverReducer = (state = false, action) => {
  action.type === "SET_GAMEOVER_STATUS" ? action.payload : state;
};

export const gameStatusReducer = (state = false, action) => {
  action.type === "SET_GAME_STATUS" ? action.payload : state;
};

const loadingReducer = (state = false, action) => {
  action.type === "SET_LOADING_STATUS" ? action.payload : state;
};

export default combineReducers({
  difficulty: difficultyReducer,
  gameStatus: gameStatusReducer,
  loading: loadingReducer,
  questionList: questionListReducer,
  gameover: gameoverReducer,
});
