import React, { useState, useEffect } from "react";

import { fetchData } from "../api/api";
const GamePage = () => {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cardNr, setCardNr] = useState(0);
  const [gameCard, setGameCard] = useState({
    question: "",
    incorrectAnswers: [],
    correctAnswer: "",
  });

  const checkAnswer = () => {
    console.log();
  };

  useEffect(async () => {
    const newCard = await fetchData();
    setGameCard(newCard);
    console.log(newCard);
  }, []);

  setTimeout(() => {
    setLoading(false);
  }, 5000);

  if (loading === true) {
    return (
      <div className="game-page">
        <div>Loading</div>
        <div className="loader"></div>
      </div>
    );
  } else {
    return (
      <div className="game-page">
        <div className="game-content">
          <div
            dangerouslySetInnerHTML={{ __html: gameCard[cardNr].question }}
            className="game-question"
          ></div>
          <div className="game-answers">
            {gameCard[cardNr].answers.map((answer) => {
              return (
                <button onClick={checkAnswer} className="answer-button">
                  {answer}
                </button>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
};

export default GamePage;
