import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getQuestionList } from "../actions/getQuestionList";
import { setLoadingStatus } from "../actions/index";
import Loading from "../components/Loading";

const GamePage = ({
  getQuestionList,
  difficulty,
  questionList,
  loading,
  setLoadingStatus,
}) => {
  let answersArray;

  const checkAnswer = (e) => {
    if (e.currentTarget === questionList[0].correct_answer) {
      return console.log("correct");
    }
  };

  useEffect(() => {
    setLoadingStatus(true);
    getQuestionList(difficulty);
  }, []);

  if (questionList[0] != undefined) {
    setLoadingStatus(false);
    answersArray = [
      ...questionList[0].incorrect_answers,
      questionList[0].correct_answer,
    ];
  }

  if (loading === true) {
    return <Loading />;
  } else {
    return (
      <div className="game-page">
        <div className="game-content">
          <div className="game-question">{questionList[0].question}</div>
          <div className="game-answers">
            {answersArray.map((answer) => {
              return (
                <button
                  dangerouslySetInnerHTML={{ __html: answer }}
                  value={answer}
                  onClick={(e) => console.log(checkAnswer)}
                  className="answer-button"
                ></button>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, { getQuestionList, setLoadingStatus })(
  GamePage
);
