import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getQuestionList } from "../actions/getQuestionList";

const GamePage = ({ getQuestionList, difficulty, questionList }) => {
  useEffect(() => {
    getQuestionList(difficulty);
  }, []);

  return (
    <div className="game-page">
      <div className="game-content">
        <div
          dangerouslySetInnerHTML={{ __html: questionList[0].question }}
          className="game-question"
        ></div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, { getQuestionList })(GamePage);
