import React from "react";
import { connect } from "react-redux";

import {
  setDifficutyMessage,
  setDifficutyLevel,
  setGameStatus,
  setLoadingStatus,
} from "../actions";

const StartButton = ({ setGameStatus, difficultyLevel, setLoadingStatus }) => {
  const startGame = () => {};

  return (
    <div>
      <button
        onClick={() => {
          setLoadingStatus(true);
          setGameStatus("game-page");
        }}
        className="button-difficulty start-button"
      >
        Start Game
      </button>
    </div>
  );
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, {
  setGameStatus,
  setLoadingStatus,
  setDifficutyMessage,
})(StartButton);
