import React, { useState, useEffect } from "react";
import Loading from "../components/Loading";
import { fetchData } from "../api/api";
const GamePage = () => {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cardNr, setCardNr] = useState(0);
  const [gameCard, setGameCard] = useState({
    question: "",
    incorrectAnswers: [],
    correctAnswer: "",
  });

  const correctAnswer = () => {
    return setTimeout(() => {
      setLoading(true);
      setCardNr(cardNr + 1);
      console.log(cardNr);
    }, 1000);
  };

  const checkAnswer = (e) => {
    if (e.currentTarget.value === gameCard[cardNr].correct_answer) {
      e.currentTarget.classList.add("right-answer");
      correctAnswer();
    } else {
      e.currentTarget.classList.add("wrong-answer");
    }
  };

  useEffect(async () => {
    const newCard = await fetchData();

    setGameCard(newCard);
  }, []);

  setTimeout(() => {
    setLoading(false);
  }, 4000);

  if (loading === true) {
    return (
      <div className="game-page">
        <Loading />
      </div>
    );
  } else {
    return (
      <div className="game-page">
        <div className="game-content">
          <div
            dangerouslySetInnerHTML={{ __html: gameCard[cardNr].question }}
            className="game-question"
          ></div>
          <div className="game-answers">
            {gameCard[cardNr].answers.map((answer) => {
              return (
                <button
                  value={answer}
                  onClick={(e) => checkAnswer(e)}
                  className="answer-button"
                >
                  {answer}
                </button>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
};

export default GamePage;
