import React, { useEffect } from "react";
import { connect } from "react-redux";
import { setGameStatus } from "../actions";
const StartButton = ({ setGameStatus, loading }) => {
  useEffect(() => {}, [loading]);

  return (
    <div>
      <button
        onClick={() => setGameStatus(true)}
        className="button-difficulty start-button"
      >
        Start Game
      </button>
    </div>
  );
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, { setGameStatus })(StartButton);
