import React, { useState } from "react";
import { connect } from "react-redux";
import {
  setDifficultyEasy,
  setDifficultyMedium,
  setDifficultyHard,
} from "../actions";
const StartPage = ({}) => {
  return (
    <div className="page">
      <div className="difficulty-title">Difficulty</div>
      <div className="pick-difficulty">
        <p className="difficulty"></p>
        <button
          onClick={() => this.props.setDifficultyEasy()}
          className="button-difficulty"
        >
          Easy
        </button>
        <button
          onClick={() => this.props.setDifficultyMedium()}
          className="button-difficulty"
        >
          Medium
        </button>
        <button
          onClick={() => this.props.setDifficultyHard()}
          className="button-difficulty"
        >
          Hard
        </button>
        <button className="button-difficulty start-button">Start Game</button>
      </div>
    </div>
  );
};

export default connect(null, {
  setDifficultyEasy,
  setDifficultyMedium,
  setDifficultyHard,
})(StartPage);
