import React, { useState, useEffect } from "react";

const GamePage = () => {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [cardNr, setCardNr] = useState(0);
  const [gameCard, setGameCard] = useState({
    question: "",
    incorrectAnswers: [],
    correctAnswer: "",
  });
  useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=10&difficulty=easy")
      .then((response) => response.json())
      .then((response) => setCards([...response.results]))
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    setGameCard({
      question: cards[cardNr].question,
      answers: [
        ...cards[cardNr].incorrect_answers,
        cards[cardNr].correctAnswer,
      ],
    });
  }, [setCards]);

  setTimeout(() => {
    setLoading(false);
  }, 5000);

  if (loading === true) {
    return (
      <div className="game-page">
        <div>Loading</div>
        <div className="loader"></div>
      </div>
    );
  } else {
    return (
      <div className="game-page">
        <div className="content">
          <div
            dangerouslySetInnerHTML={{ __html: cards[cardNr].question }}
          ></div>
        </div>
      </div>
    );
  }
};

export default GamePage;
