export default (state = { difficulty: "easy" }, action) => {
  switch (action.type) {
    case "DIFFICULTY_EASY":
      return { difficulty: action.payload };
    case "DIFFICULTY_MEDIUM":
      return { difficulty: action.payload };
    case "DIFFICULTY_HARD":
      return { difficulty: action.payload };
    default:
      return state;
  }
};
