import React from "react";
import { connect } from "react-redux";
import { getQuestionList } from "../actions/getQuestionList";
import { shuffleArray } from "../utils/utils";
import { setGameStatus, setQuestionNumber } from "../actions/index";
import { Howl, Howler } from "howler";
import correctAnswerSound from "../sounds/Correct-answer.mp3";

import DOMPurity from "dompurify";

const GameCard = ({
  questionList,
  questionNumber,
  setQuestionNumber,
  setGameStatus,
}) => {
  let answersArray = shuffleArray([
    ...questionList[questionNumber].incorrect_answers,
    questionList[questionNumber].correct_answer,
  ]);

  if (questionNumber === 9) {
    setGameStatus("game-over-page");
    setQuestionNumber(0);
  }

  const correctAnswer = new Howl({
    src: [correctAnswerSound],
  });

  const checkAnswerResults = () => {};

  const addClassName = (element, name) => {
    element.classList.add(name);
    element.parentNode.parentNode.classList.add("disabled");
  };

  const removeClassName = (element, name) => {
    element.classList.remove(name);
    element.parentNode.parentNode.classList.remove("disabled");
  };
  const checkAnswer = (e) => {
    let answerButton = e.currentTarget;
    if (answerButton.value === questionList[questionNumber].correct_answer) {
      addClassName(answerButton, "right-answer");
      setTimeout(() => {
        removeClassName(answerButton, "right-answer");
        setQuestionNumber(questionNumber);
      }, 2000);
    } else {
      addClassName(answerButton, "wrong-answer");
      setTimeout((e) => {
        answerButton.classList.remove("wrong-answer");
        answerButton.parentNode.parentNode.classList.remove("disabled");
        setQuestionNumber(questionNumber);
      }, 2000);
    }
  };

  return (
    <div className="game-page">
      <div className="game-content">
        <div
          dangerouslySetInnerHTML={{
            __html: DOMPurity.sanitize(questionList[questionNumber].question),
          }}
          className="game-question"
        ></div>
        <div className="game-answers">
          {answersArray.map((answer) => {
            return (
              <button
                dangerouslySetInnerHTML={{
                  __html: DOMPurity.sanitize(answer),
                }}
                value={answer}
                onClick={(e) => checkAnswer(e)}
                className="answer-button"
              ></button>
            );
          })}
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, {
  getQuestionList,
  setQuestionNumber,
  setGameStatus,
})(GameCard);
