import { combineReducers } from "redux";
const returnActionPayload = (type) => (state = null, action) => {
  return action.type === "SET_GAMEOVER_STATUS" ? action.payload : state;
};

const gameoverReducer = returnActionPayload("SET_GAMEOVER_STATUS");
const difficultyReducer = returnActionPayload("DIFFICULTY_LEVEL");

const gameStatusReducer = (state = false, action) => {
  return action.type === "SET_GAME_STATUS" ? action.payload : state;
};

const loadingReducer = (state = false, action) => {
  return action.type === "SET_LOADING_STATUS" ? action.payload : state;
};

const questionListReducer = (state = [], action) => {
  switch (action.type) {
    case "GET_QUESTIONS_LIST":
      return [...state, action.payload];
    default:
      return state;
  }
};

export default combineReducers({
  difficulty: difficultyReducer,
  gameStatus: gameStatusReducer,
  loading: loadingReducer,
  questionList: questionListReducer,
  gameover: gameoverReducer,
});
