import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getQuestionList } from "../actions/getQuestionList";
import { shuffleArray } from "../utils/utils";

import {
  setGameStatus,
  setLoadingStatus,
  setQuestionNumber,
} from "../actions/index";
import Loading from "../components/Loading";
import DOMPurity from "dompurify";

const GamePage = ({
  getQuestionList,
  difficulty,
  questionList,
  loading,
  setLoadingStatus,
  questionNumber,
  setQuestionNumber,
  setGameStatus,
}) => {
  useEffect(() => {
    setLoadingStatus(true);
    getQuestionList(difficulty);
  }, []);

  //Wait for the action to fetch the questions
  questionList[0] !== undefined
    ? setLoadingStatus(false)
    : setLoadingStatus(true);

  if (loading == true) {
    return <Loading />;
  }

  let answersArray = shuffleArray([
    ...questionList[questionNumber].incorrect_answers,
    questionList[questionNumber].correct_answer,
  ]);

  return <div>hello</div>;
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, {
  getQuestionList,
  setLoadingStatus,
  setQuestionNumber,
  setGameStatus,
})(GamePage);
