import React, { useState, useEffect } from "react";
import Loading from "../components/Loading";
import { fetchData } from "../api/api";
import GameOver from "../components/GameOver";

const GamePage = () => {
  //Set Loading to false
  setTimeout(() => {
    setLoading(false);
  }, 3500);

  // Load Card
  useEffect(() => {
    const getData = async () => {
      const newCard = await fetchData();
      setGameCard(newCard);
    };
    getData();
  }, []);

  //Correct Wrong answers
  const correctAnswer = () => {
    return setTimeout(() => {
      setquestionNr(questionNr + 1);
      setCorrect(correct + 1);
      setLoading(true);
    }, 2500);
  };

  const wrongAnswer = () => {
    return setTimeout(() => {
      setquestionNr(questionNr + 1);
      setIncorrect(incorrect + 1);
      setLoading(true);
    }, 2500);
  };

  //Check answer
  const checkAnswer = (e) => {
    if (e.currentTarget.value === gameCard[questionNr].correct_answer) {
      e.currentTarget.classList.add("right-answer");
      correctAnswer();
    } else {
      e.currentTarget.classList.add("wrong-answer");
      wrongAnswer();
    }
  };

  //Loading Screen
  if (loading === true) {
    return (
      <div className="game-page">
        <Loading />
      </div>
    );
    //Game Over
  } else if (questionNr + 1 === 10) {
    return <GameOver />;
  } else {
    //Game Card
    return (
      <div className="game-page">
        <div className="game-content">
          <div
            dangerouslySetInnerHTML={{ __html: gameCard[questionNr].question }}
            className="game-question"
          ></div>
          <div className="game-answers">
            {gameCard[questionNr].answers.map((answer) => {
              return (
                <button
                  dangerouslySetInnerHTML={{ __html: answer }}
                  value={answer}
                  onClick={(e) => checkAnswer(e)}
                  className="answer-button"
                ></button>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
};

export default GamePage;
