import React, { useState, useEffect } from "react";
import { Link, withRouter } from "react-router-dom";
import Loading from "../components/Loading";

const StartPage = () => {
  const [difficulty, setDifficulty] = useState("Please choose difficulty !!!");
  const [loading, setLoading] = useState(true);

  setTimeout(() => {
    setLoading(false);
  }, 3000);

  if (loading === true) {
    return (
      <div className="page-game">
        <Loading />
      </div>
    );
  }
  return (
    <div className="starting-page">
      <div className="difficulty-title">Difficulty</div>
      <div className="pick-difficulty">
        <p className="difficulty">{difficulty}</p>
        <button
          onClick={() => setDifficulty("Easy")}
          className="button-difficulty"
        >
          Easy
        </button>
        <button
          onClick={() => {
            setDifficulty("Medium");
          }}
          className="button-difficulty"
        >
          Medium
        </button>
        <button
          onClick={() => setDifficulty("Hard")}
          className="button-difficulty"
        >
          Hard
        </button>
        <Link to={"/gamepage"}>
          <button className="button-difficulty start-button">Start Game</button>
        </Link>
      </div>
    </div>
  );
};

export default withRouter(StartPage);
