import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Loading from "../components/Loading";
import { fetchData } from "../api/api";
import { succesRate } from "../utils/utils";

const GamePage = () => {
  //React Hooks
  const [loading, setLoading] = useState(true);
  const [questionNr, setquestionNr] = useState(0);
  const [gameCard, setGameCard] = useState({});
  const [correct, setCorrect] = useState(0);
  const [incorrect, setIncorrect] = useState(0);

  //Set Loading to false
  setTimeout(() => {
    setLoading(false);
  }, 3500);

  const getData = async () => {
    const newCard = await fetchData();
    setGameCard(newCard);
  };
  // Load Card
  useEffect(() => {
    const getData = async () => {
      const newCard = await fetchData();
      setGameCard(newCard);
    };
    getData();
  }, []);

  //Correct Wrong answers
  const correctAnswer = () => {
    return setTimeout(() => {
      setquestionNr(questionNr + 1);
      setCorrect(correct + 1);
      setLoading(true);
    }, 3000);
  };
  const wrongAnswer = () => {
    return setTimeout(() => {
      setquestionNr(questionNr + 1);
      setIncorrect(incorrect + 1);
      setLoading(true);
    }, 3000);
  };

  //Check answer
  const checkAnswer = (e) => {
    if (e.currentTarget.value === gameCard[questionNr].correct_answer) {
      e.currentTarget.classList.add("right-answer");
      correctAnswer();
    } else {
      e.currentTarget.classList.add("wrong-answer");
      wrongAnswer();
    }
  };

  //Loading Screen
  if (loading === true) {
    return (
      <div className="game-page">
        <Loading />
      </div>
    );
    //Game Over
  } else if (questionNr + 1 === 10) {
    return (
      <div className="game-page">
        <div className="game-over-page">
          <div className="game-over-title">
            <h1>Game over</h1>
          </div>
          <div className="succes-rate">
            <h1>{succesRate(correct, 10)}%</h1>
          </div>

          <div>
            <Link to={"/"}>
              <button className="button-difficulty start-button">
                Play Again
              </button>
            </Link>
          </div>
        </div>
      </div>
    );
  } else {
    //Game Card
    return (
      <div className="game-page">
        <div className="game-content">
          <div
            dangerouslySetInnerHTML={{ __html: gameCard[questionNr].question }}
            className="game-question"
          ></div>
          <div className="game-answers">
            {gameCard[questionNr].answers.map((answer) => {
              return (
                <button
                  dangerouslySetInnerHTML={{ __html: answer }}
                  value={answer}
                  onClick={(e) => checkAnswer(e)}
                  className="answer-button"
                ></button>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
};

export default GamePage;
