import React from "react";
import { connect } from "react-redux";
import { getQuestionList } from "../actions/getQuestionList";
import { shuffleArray } from "../utils/utils";
import {
  setGameStatus,
  setLoadingStatus,
  setQuestionNumber,
} from "../actions/index";

import DOMPurity from "dompurify";

const GameCard = ({
  questionList,
  loading,
  setLoadingStatus,
  questionNumber,
  setQuestionNumber,
  setGameStatus,
}) => {
  let answersArray = shuffleArray([
    ...questionList[questionNumber].incorrect_answers,
    questionList[questionNumber].correct_answer,
  ]);

  if (questionNumber === 9) {
    return setGameStatus("game-over-page");
  }

  const checkAnswer = (e) => {
    if (questionNumber === 9) {
      setQuestionNumber(0);
      setGameStatus("game-over-page");
    }

    let answerButton = e.currentTarget;
    if (answerButton.value === questionList[questionNumber].correct_answer) {
      answerButton.classList.add("right-answer");
      answerButton.parentNode.parentNode.classList.add("disabled");
      setTimeout(() => {
        answerButton.classList.remove("right-answer");
        answerButton.parentNode.parentNode.classList.remove("disabled");
        setQuestionNumber(questionNumber + 1);
      }, 2000);
    } else {
      answerButton.classList.add("wrong-answer");
      answerButton.parentNode.parentNode.classList.add("disabled");
      setTimeout((e) => {
        answerButton.classList.remove("wrong-answer");
        answerButton.parentNode.parentNode.classList.remove("disabled");
        setQuestionNumber(questionNumber + 1);
      }, 2000);
    }
  };

  return (
    <div className="game-page">
      <div className="game-content">
        <div
          dangerouslySetInnerHTML={{
            __html: DOMPurity.sanitize(questionList[questionNumber].question),
          }}
          className="game-question"
        ></div>
        <div className="game-answers">
          {answersArray.map((answer) => {
            return (
              <button
                dangerouslySetInnerHTML={{
                  __html: DOMPurity.sanitize(answer),
                }}
                value={answer}
                onClick={(e) => checkAnswer(e)}
                className="answer-button"
              ></button>
            );
          })}
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return state;
};

export default connect(mapStateToProps, {
  getQuestionList,
  setLoadingStatus,
  setQuestionNumber,
  setGameStatus,
})(GameCard);
